  constructor() {
    super();
    this.state = { herois: null, show: false };
    this.hero = null;
  }

  componentDidMount() {
    window.addEventListener('scroll', () => {
      if (!this.hero) return;
      const sectionTop = this.hero.getBoundingClientRect().top;
